INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6792, 'Kast 10mg 10 f.c. tab', 'كاست 10مجم 10 اقراص', '59', '52', 'Asthma.anti-inflammatory.leukotriene antagonist', 'Montelukast', 'Hikma pharma', 'Tab', 'أقراص', '10mg', '1', 'علاج أزمات الصدر وصعوبة وضيق التنفس', '', '6221000000867', '', 6347, '2025-01-22', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Montelukast was first approved for clinical use by the US FDA in 1998 as Merck''s brand name Singulair.[L6301] The medication is a member of the leukotriene receptor antagonist (LTRA) category of drugs.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Although capable of demonstrating effectiveness, the use of such LTRAs like montelukast is typically in addition to or complementary with the use of inhaled corticosteroids or other agents in asthma step therapy.[A178645] Regardless, in 2008-2009, there were FDA-led investigations into the possibility of montelukast to elicit neuropsychiatric effects like agitation, hallucinations, suicidal behaviour, and others in individuals who used the medication.[A178651] And although these kinds of effects are currently included in the official prescribing information for montelukast,[L6301,L6304,L6307,L6310,L6325,L6328,L6331] the drug still sees extensive use worldwide via millions of prescriptions annually and has since become available as a generic and as a brand name product.', '["R03DC03", "R03DC53"]', '[]');
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6794, 'Kast 5 mg 14 chew. tab.', 'كاست 5مجم 14 قرص مضغ', '53', '42', 'Asthma.anti-inflammatory.leukotriene antagonist', 'Montelukast', 'Hikma pharma', 'Tab', 'أقراص', '5 mg', '2', '', 'about montelukast leukotriene(cyslt1) receptor antagonist antiinflammatory antiasthma. mechanism of action of montelukast montelukast is competitively and selectively antagonizes cyslt1 receptor (leukotrienes receptor) mediated bronchoconstriction.montelu', '6221000000874', '', 3105, '2023-09-17', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Montelukast was first approved for clinical use by the US FDA in 1998 as Merck''s brand name Singulair.[L6301] The medication is a member of the leukotriene receptor antagonist (LTRA) category of drugs.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Although capable of demonstrating effectiveness, the use of such LTRAs like montelukast is typically in addition to or complementary with the use of inhaled corticosteroids or other agents in asthma step therapy.[A178645] Regardless, in 2008-2009, there were FDA-led investigations into the possibility of montelukast to elicit neuropsychiatric effects like agitation, hallucinations, suicidal behaviour, and others in individuals who used the medication.[A178651] And although these kinds of effects are currently included in the official prescribing information for montelukast,[L6301,L6304,L6307,L6310,L6325,L6328,L6331] the drug still sees extensive use worldwide via millions of prescriptions annually and has since become available as a generic and as a brand name product.', '["R03DC03", "R03DC53"]', '[]');
