INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6823, 'Nerhafluks 20 mg 20 odf', 'نيرهافلوكس 20 مجم 20 فيلم ذائب على اللسان', '90', '', 'Peptic ulcer.anti-histamine.h2 antagonist', 'Famotidine', 'Nerhadou international co.', 'Unknown', 'غير محدد', '20 mg', '2', '', '', '6224011153379', '', 986, '2025-08-15', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Famotidine is a competitive histamine-2 (H2) receptor antagonist that works to inhibit gastric acid secretion. It is commonly used in gastrointestinal conditions related to acid secretion, such as gastric ulcers and gastroesophageal reflux disease (GERD), in adults and children.[L11166] Compared to other H2receptor antagonists, famotidine displays high selectivity towards this receptor; in a study consisting of healthy volunteers and patients with acid hypersecretory disease, famotidine was about 20 to 50 times more potent at inhibiting gastric acid secretion than [cimetidine] and eight times more potent than [ranitidine] on a weight basis.[A189462] Famotidine is used in various over-the-counter and off-label uses.[L11166] While oral formulations of famotidine are more commonly used, the intravenous solution of the drug is available for use in hospital settings.[L11142]', '["A02BA03", "A02BA53"]', '[]');
