INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6830, 'Kempoxone 1 gm i.m. vial', 'كيمبوكسون 1جم فيال عضل', '56', '39', 'Antibiotic.cephalosporin.third-generation', 'Ceftriaxone', 'Kahira > modern office for import', 'Vial', 'فيال', '1 gm', '1', '', '', '6224010511057', '', 2476, '2024-06-29', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Ceftriaxone is a broad-spectrum third-generation cephalosporin antibiotic.[A215582] It has a very long half-life compared to other cephalosporins and is high penetrable into the meninges[A215582], eyes[A215647], and inner ear[A215627]. Ceftriaxone has broader and stronger gram-negative coverage then first or second-generation cephalosporins, but worse activity against methicillin-susceptible S.aureus. Ceftriaxone is a commonly used antimicrobial due to its good activity against multi-drug resistant Enterobacteriaceae, its relatively safe adverse effect profile, and its long half-life which allows for the convenience of daily or twice-daily dosing.[A215582]', '["J01DD04", "J01DD54"]', '[]');
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6831, 'Kemprost sr 0.4 mg 10 caps', 'كيمبروست ممتد المفعول 0.4 مجم 10 كبسولات', '56', '', 'Urination-difficulty.alpha blocker', 'Tamsulosin', 'Chemipharm', 'Cap', 'كبسولة', '0.4 mg', '1', 'علاج صعوبة التبول __ تقليل الالام أثناء التبول __ علاج تضخم البروستاتا', '', '6222006500764', '', 2138, '2022-04-18', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Tamsulosin is a selective alpha-1A and alpha-1B adrenoceptor antagonist that exerts its greatest effect in the prostate and bladder, where these receptors are most common.[Label] It is indicated for the treatment of signs and symptoms of benign prostatic hypertrophy.[Label] Antagonism of these receptors leads to relaxation of smooth muscle in the prostate and detrusor muscles in the bladder, allowing for better urinary flow.[Label] Other alpha-1 adrenoceptor antagonists developed in the 1980s were less selective and more likely to act on the smooth muscle of blood vessels, resulting in hypotension.[A178351]
