INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (6996, 'Rybelsus 7mg 10 tabs', 'ريبلسوس 7مجم 10 اقراص', '1,575', '1050', 'Anti-diabetic.glp-1 receptor agonist', 'Semaglutide', 'Novo nordisk', 'Tab', 'أقراص', '7mg', '1', 'بديل اوزمبك لعمليات التخسيس والتنحيف--ريبلسوس: بديل الحقن الجديد لعلاج مرض السكري--ريبلسوس او سيماجلوتايد--ريبلسوس أو سيماجلوتيدهو عقار تم تطويره ليصبح كببتيد شبيه الجلوكاجون 1، وهو هرمون ببتيدي طويل يتكون من 30 حمض أميني يُفرز بشكل طبيعي في الجسم عند تناول الطعام، ويعمل مع الأنسولين على خفض مستويات السكر في الدم عن طريق تعزيز إفراز الأنسولين من خلايا بيتا في البنكرياس.تمت الموافقة على سيماجلوتيد من قبل إدارة الغذاء و الدواء الأمريكية (FDA) في عام 2017 كمنتج للحقن تحت الجلد لعلاج مرض السكري من النوع الثاني، ولاحقاً في عام 2019 تم إصدار أقراص من سيماجلوتيد لتكون بديلة عن الحقن لمساعدة مرضى السكري الذين لا يرغبون في استخدام أقلام الحقن في السيطرة على مستوى السكر في الدم.إرشادات يجب مراعاتها عند استخدام أقراص ريبلسوسأخبر طبيبك إذا كان لديك حساسية من المادة الفعالة أو أي مادة من مكونات الدواء.لا تتناول سيماجلوتيد إذا كنت تعاني من داء السكري النوع الأول أو أي مشكلة في الدم أو التهاب البنكرياس.أخبر طبيبك إن كنت تتناول أدوية أخرى أو مكملات غذائية.لا تأخذي هذا الدواء إذا كنتي حاملاً أو مرضعاً.اتباع حمية غذائية ونمط حياة صحي إلى جانب العلاج يساعد على السيطرة على مستوى منخفض من السكر في الدم، وفي حال عدم النجاح في السيطرة على مستوى السكر فإن الحالة تستدعي زيادة في الجرعة أو استخدام علاج آخر إلى جانب ريبلسوس وفق ما يرى الطبيب المعالج.لا تستخدم هذا الدواء إذا أصبت من قبل بأمراض في الغدة الدرقية أو ان كان في تاريخ العائلة إصابة بسرطان الغدة الدرقية.يؤخذ الدواء قبل 30 دقيقة من تناول أي شيء صباحاً ويجب عدم تناول المشروبات التي تحتوي على الكافيين معه.يجب الالتزام بالجرعة اليومية حتى وإن شعر المريض بأن مستوى السكر لديه طبيعي وأنه بغنى عنها.في حال تذكر المريض أنه لم يأخذ جرعة اليوم السابق فإنه يلتزم بالجرعة الحالية فقط ولا يجب أن يقوم بمضاعفة الجرعة.في حال كانت الجرعة اليومية قرص واحد 14 ملغم فإنه يستحسن أحذ قرص واحد وعدم الاعتماد على أخذ قرصين 7 ملغم وتقسيم الجرعة على فترات.', '', '', '', 1399, '2024-09-07', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Semaglutide is a glucagon-like peptide 1 (GLP-1) analog used to manage type 2 diabetes along with lifestyle changes, such as dietary restrictions and increased physical activity.[A31421,L8681] Other members of this drug class include [Exenatide] and [Liraglutide]. Semaglutide was developed by Novo Nordisk and approved by the FDA for subcutaneous injection in December 2017.[L8681] The tablet formulation was approved for oral administration in September 2019. Semaglutide works by binding to and activating the GLP-1 receptor, thereby stimulating insulin secretion and reducing blood glucose.[L8678]
