INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (7245, 'Levemir 100 i.u./ml 5 flexpen', 'ليفمير 100 وحدة دولية / مل 5 اقلام معبأة للحقن', '788', '', 'Anti-diabetic.insulin analouge', 'Insulin detemir', 'Novo nordisk', 'Pen', 'قلم', '', '5', '', '', '', '', 2996, '2022-08-08', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Insulin detemir is a long-acting form of insulin used for the treatment of hyperglycemia caused by Type 1 and Type 2 Diabetes.[A2358, A2359, A2360, A44] Insulin is typically prescribed for the management of diabetes mellitus to mimic the activity of endogenously produced human insulin, a peptide hormone produced by beta cells of the pancreas that promotes glucose metabolism.[A2358, A2359, A2360, A44] Insulin is released from the pancreas following a meal to promote the uptake of glucose from the blood into internal organs and tissues such as the liver, fat cells, and skeletal muscle.[A2358, A2359, A2360, A44] The absorption of glucose into cells allows for its transformation into glycogen or fat for storage.[A2358, A2359, A2360, A44] Insulin also inhibits hepatic glucose production, enhances protein synthesis, and inhibits lipolysis and proteolysis among many other functions.[A2358, A2359, A2360, A44]
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (7246, 'levepex 1000 mg 30 f.c. tab.', 'ليفيبكس 1000مجم 30 قرص', '288', '216', 'Anti-epileptic.pyrrolidine', 'levetiracetam', 'multi-apex', 'tablet', 'أقراص', '1000 mg', '3', '', 'about levetiracetam it is s-enantiomer of etiracetam. anticonvulsant medication used to treat epilepsy. mechanism of action of levetiracetam the exact mechanism for levetiracetam is unknown. however the drug binds to a synaptic vesicle protein sv2a which ', '6223003204146', '', 2002, '2025-12-17', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Levetiracetam is a drug within the pyrrolidine class that is used to treat various types of seizures stemming from epileptic disorders. It was first approved for use in the United States in 1999 and is structurally and mechanistically unrelated to other anti-epileptic drugs (AEDs).[L8606,L8600,L8615] Levetiracetam possesses a wide therapeutic index[L8615,A185918] and little-to-no potential to produce, or be subject to, pharmacokinetic interactions[L8606,L8600,L8615] - these characteristics make it a desirable choice over other AEDs, a class of drugs notorious for having generally narrow therapeutic indexes and a propensity for involvement in drug interactions.[A185927]', '["N03AX14"]', '[]');
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (7247, 'Traviguard 200 mg 20 f.c. tabs.', 'ترافي جارد 200 مجم 20 قرص', '68', '', 'Travelers diarrhea', 'Rifaximin', 'Trend pharm', 'Tab', 'أقراص', '200 mg', '2', '', '', '6223003203085', '', 961, '2023-06-07', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Rifaximin is a semisynthetic, rifamycin-based non-systemic antibiotic, meaning that the drug will not pass the gastrointestinal wall into the circulation as is common for other types of orally administered antibiotics. It has multiple indications and is used in treatment of traveller''s diarrhea caused by E. coli; reduction in risk of overt hepatic encephalopathy recurrence; as well as diarrhea-predominant irritable bowel syndrome (IBS-D) in adult women and men. It is marketed under the brand name Xifaxan by Salix Pharmaceuticals.', '["A07AA11", "D06AX11"]', '[]');
