INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (8095, 'Methyldopa 250mg 30 f.c. tabs.', 'ميثيل دوبا 250 مجم 30 قرص', '111', '48', 'Antihypertensives.centeral alpha 2 agonist', 'Methyldopa', 'Alexandria', 'Tab', 'أقراص', '250mg', '3', '', 'about methyldopa centrally acting alpha-2 agonist a phenylalanine derivative antihypertensive (pregnancy-induced hypertension). mechanism of action of methyldopa methyl dopa gets metabolized in the adrenergic neurons to alpha methyl nor adrenaline which a', '6223005443106', '', 1967, '2024-09-26', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Methyldopa, or α-methyldopa, is a centrally acting sympatholytic agent and an antihypertensive agent.[A231784] It is an analog of DOPA (3,4‐hydroxyphenylanine), and it is a prodrug, meaning that the drug requires biotransformation to an active metabolite for therapeutic effects. Methyldopa works by binding to alpha(α)-2 adrenergic receptors as an agonist, leading to the inhibition of adrenergic neuronal outflow and reduction of vasoconstrictor adrenergic signals.[A1499] Methyldopa exists in two isomers D-α-methyldopa and L-α-methyldopa, which is the active form.[A232224]
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (8097, 'Metoclopramide 10mg/2ml 5 amp. for iv/im inj.', 'ميتوكلوبراميد 10مجم/2مل 5 امبولات', '8', '', 'Dopamine antagonist', 'Metoclopramide', 'Sedico', 'Inj', 'حقن', '10mg', '5', '', 'about metoclopramide it is a dopamine antagonist - antiemetic gastroprokinetic agent. mechanism of action of metoclopramide metoclopramide acts through both serotonergic and dopaminergic receptors. metoclopramide binds to serotonin (5 ht3 &5ht4) and dopam', '6221042428056', '', 2421, '2022-12-06', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Diabetic gastroparesis is a condition that causes frequent nausea and vomiting, which has a negative impact on quality of life and poses a significant burden on the healthcare system.[A184934] Metoclopramide is a dopamine antagonist used to treat nausea and vomiting that may be associated with diabetic gastroparesis in addition to gastroesophageal reflux disease (GERD). It can also be used to prevent nausea or vomiting associated with chemotherapy or certain surgical or diagnostic procedures.[L8417] 
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (8100, 'Metrohaler 12 mcg 30 cap+inhaler', 'متروهيلر 30 كبسولة استنشاق + جهاز استنشاق', '172', '130', 'Bronchodilator.long acting beta 2 agonist', 'Formoterol fumarate', 'European egyptian pharm. ind.', 'Cap', 'كبسولة', '12 mcg', '3', 'بخاخ موسعة للشعب الهوائية __ تساعد علي تحسين التنفس __ تقلل الكحة الناتجة من الأزمات التنفسية __', 'mechanism of action the pharmacologic effects of beta2-adrenoceptor agonist drugs including formoterol are at least in part attributable to stimulation of intracellular adenyl cyclase the enzyme that catalyzes the conversion of adenosine triphosphate (atp', '6221076020530', '', 9204, '2024-08-30', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Formoterol is an inhaled beta2-agonist used in the management of COPD and asthma that was first approved for use in the United States in 2001.[L10986] It acts on bronchial smooth muscle to dilate and relax airways, and is administered as a racemic mixture of its active (R;R)- and inactive (S;S)-enantiomers.[A189528] A major clinical advantage of formoterol over other inhaled beta-agonists is its rapid onset of action (2-3 minutes), which is at least as fast as [salbutamol], combined with a long duration of action (12 hours) - for this reason, treatment guidelines for asthma recommend its use as both a reliever and maintenance medication.[L11256] It is available as a single-entity product [L10986,L11223] and in several formulations in combination with both inhaled corticosteroids [L10995,L10619] and long-acting muscarinic antagonists.[L10992,L10989]', '["R03AL09", "R03AL05", "R03AK09", "R03AL10", "R03CC15", "R03AL11", "R03AL07", "R03AK08", "R03AC13", "R03AK07", "R03AK11"]', '[]');
