INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (8300, 'Monopril 10mg 10 tab.', 'مونوبريل 10مجم 10 اقراص', '19', '', 'Antihypertensive.ace', 'Fosinopril', 'Smithkline beecham', 'Tab', 'أقراص', '10mg', '1', '', 'about fosinopril phosphonate-containing angiotensin-converting enzyme (ace) inhibitor antihypertensive. mechanism of action of fosinopril it is a prodrug and converted in to active fosinoprilat; which is an angiotensin converting enzyme inhibitor prevents', '6224001007330', '', 866, '2022-12-07', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Fosinopril is a phosphinic acid-containing ester prodrug that belongs to the angiotensin-converting enzyme (ACE) inhibitor class of medications. It is rapidly hydrolyzed to fosinoprilat, its principle active metabolite. Fosinoprilat inhibits ACE, the enzyme responsible for the conversion of angiotensin I (ATI) to angiotensin II (ATII). ATII regulates blood pressure and is a key component of the renin-angiotensin-aldosterone system (RAAS). Fosinopril may be used to treat mild to moderate hypertension, as an adjunct in the treatment of congestive heart failure, and to slow the rate of progression of renal disease in hypertensive individuals with diabetes mellitus and microalbuminuria or overt nephropathy.', '["C09BA09", "C09AA09"]', '[]');
