INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (8487, 'Mycostatin 100 000 i.u./ml susp. 30ml', 'ميكوستاتين نقط معلق 30 مل', '9', '', 'Antifungal.polyene', 'Nystatin', 'Glaxo smithkline > smithkline beecham', 'Susp', 'معلق', '30ml', '1', '', 'about nystatin polyene antifungal. mechanism of action of nystatin nystatin is a fungicidal as well as a fungistatic drug. it has high affinity for ergosterol present in the fungal cell membrane and bind with it and they orients in such a way that micropo', '6224001007002', '', 4582, '2022-12-06', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Nystatin is a polyene antifungal drug that has broad-spectrum fungicidal and fungistatic activity against a number of yeasts and fungi, most notably _Candida_ species.[L10686] It is one of the most effective antifungal agents synthesized by bacteria, in this case a strain of _Streptomyces noursei_,[L10776] and is closely related to [amphotericin B], differing only slightly in structure.[A188562] Nystatin has a greater antifungal activity than amphotericin B - parenterally administered nystatin, however, is associated with significant toxicity and is not available in a formulation appropriate for systemic use.[A188562] As it undergoes very little absorption following oral or topical administration, nystatin''s efficacy is limited to the treatment/prevention of cutaneous, mucocutaneous, and gastrointestinal fungal infections.[L10686,L10728]', '["D01AA01", "G01AA01", "A07AA02", "G01AA51"]', '[]');
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (8491, 'Mydriacyl 1% eye drops 15 ml', 'ميدرياسيل 1% قطرة عين 15 مل', '166', '116', 'Mydriatic', 'Tropicamide', 'Novartis > novartis scientific office', 'Drops', 'نقط', '1%', '1', '', 'about tropicamide anticholinergic (muscarinic blocker) ophthalmic agent mydriatic cycloplegic. mechanism of action of tropicamide tropicamide is a muscarinic receptor blocker. it nonselectively inhibits the muscarinic actions of acetylcholine. tropicamide', '5413895003554', '', 3249, '2025-04-30', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Tropicamide is an alkaloid atropine‐derived anticholinergic drug and a non‐selective antagonist of muscarinic acetylcholine (mACh) receptors.[A230103] Usually available in ophthalmic formulations, tropicamide is used to cause mydriasis and cycloplegia for eye exams or ocular procedures.[A229958] It is also used in combination with [hydroxyamphetamine] for the same indication.[L32178] Oral tropicamide has been investigated as a potential drug to relieve sialorrhea in patients with Parkinson''s Disease.[A229958]', '["S01FA06", "S01FA56"]', '[]');
