INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (12015, 'Utrocare 100mg 30 soft gelatin caps.', 'يتروكير 100مجم 30 كبسولة', '126', '84', 'Progesterone replacement', 'Progesterone', 'Safe pharma > october pharma', 'Cap', 'كبسولة', '100mg', '3', 'هرمون البروجسترون', 'indication for progesterone supplementation or replacement as part of an assisted reproductive technology (art) treatment for infertile women with progesterone deficiency and for the treatment of secondary amenorrhea. also used for the reduction of the in', '6221088021303', '', 5303, '2024-09-26', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Progesterone is a hormone that occurs naturally in females, and is essential for endometrial receptivity, embryo implantation, and the successful establishment of pregnancy. A low progesterone concentration or an insufficient response to progesterone can cause infertility and pregnancy loss [A175609]. Progesterone is used in various contraceptive preparations to prevent ovulation and fertilization [T481], [A175612] as well as in other formulations to promote and support pregnancy.  Please see [Medroxyprogesterone acetate], [Megestrol acetate], [Dydrogesterone] and [Hydroxyprogesterone] entries for information on various other forms of progesterone.

Pharmaceutical progesterone is made from a plant source as a starting material and is chemically identical to progesterone of human ovarian origin [FDA label]. Progesterone is available in gelatinized capsule form, vaginal gel form, tablet form, vaginal insert form, and injection form, all used for various purposes [Label,F3898,F3904,F3901,F3907].', '["G03FA04", "G03DA04"]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (12017, 'Uvamine retard 100 mg 20 caps.', 'يوفامين ريتارد 100مجم 20 كبسولة', '47', '30', 'Uti infections', 'Nitrofurantoin', 'Mup > acino', 'Cap', 'كبسولة', '100 mg', '2', 'يستخدم لعدوى الجهاز البولي -- مطهر للمسالك البولية', '', '6221508020916', '', 12331, '2025-01-14', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Nitrofurantoin is a nitrofuran antibiotic used to treat uncomplicated urinary tract infections.[L6856,L6859,L6862] Nitrofurantoin is converted by bacterial nitroreductases to electrophilic intermediates which inhibit the citric acid cycle as well as synthesis of DNA, RNA, and protein.[A179824] This drug is more resistant to the development of bacterial resistance because it acts on many targets at once.[A179824] Nitrofurantoin is a second line treatment to [trimethoprim]/[sulfamethoxazole].[A179830]

Nitrofurantoin was granted FDA approval on 6 February 1953.[L6895]', '["J01XE01", "J01XE51"]', '[]');
