INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (12548, 'Vixadep 150mg 30 s.r. tablets', 'فيكساديب 150مجم 30 قرص اس آر', '221', '78', 'Psychiatric.antidepressant', 'Bupropion', 'Marcyrl', 'Tablet', 'أقراص', '150mg', '1', 'مضاد اكتئاب - أمراض نفسية', '', '6223003572754', '', 3690, '2025-01-01', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Bupropion (also known as the brand name product Wellbutrin®) is a norepinephrine/dopamine-reuptake inhibitor (NDRI) used most commonly for the management of Major Depressive Disorder (MDD), Seasonal Affective Disorder (SAD), and as an aid for smoking cessation. Bupropion exerts its pharmacological effects by weakly inhibiting the enzymes involved in the uptake of the neurotransmitters norepinephrine and dopamine from the synaptic cleft, therefore prolonging their duration of action within the neuronal synapse and the downstream effects of these neurotransmitters. More specifically, bupropion binds to the norepinephrine transporter (NET) and the dopamine transporter (DAT).[A6399,A178810]
