INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (12915, 'Zoladex 3.6mg prefilled syringe', 'زولادكس 3.6 مجم سرنجة معبأة', '2,781', '2139', 'Gnrh agonist', 'Goserelin', 'Astra zeneca', 'Syringe', 'حقنة', '3.6mg', '1', 'لعلاج حالات التهاب بطانة الرحم -- علاج هرموني في بعض اضطرابات امراض النساء', 'goserelin is a drug used to suppress production of the sex hormones (testosterone and estrogen) particularly in the treatment of breast and prostate cancer. it is an injectable gonadotropin releasing hormone superagonist (gnrh agonist) also known as a lut', '5000455018204', '01050004560552842104816925986800 1727013110SP095', 18671, '2025-06-23', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Goserelin is a synthetic hormone. In men, it stops the production of the hormone testosterone, which may stimulate the growth of cancer cells. In women, goserelin decreases the production of the hormone estradiol (which may stimulate the growth of cancer cells) to levels similar to a postmenopausal state. When the medication is stopped, hormone levels return to normal.', '["L02AE03"]', '[]');
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (12916, 'Zolam 0.25mg 30 tab.', 'زولام 0.25مجم 30 قرص', '30', '18', 'Psychiatric.anxiolytics-hypnotics', 'Alprazolam', 'Amoun', 'Tab', 'أقراص', '0.25mg', '3', '', '', '6221025009319', '', 7268, '2025-10-15', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Alprazolam is a triazolobenzodiazepine indicated for the treatment of anxiety and panic disorders.[L34783, L34788] It is mainly metabolized by CYP3As and so is contraindicated with CYP3A inhibitors like ketoconazole and itraconazole.[L34783, L34788] Benzodiazepine treatment should be stopped gradually by tapering down a patient''s dose to avoid withdrawal symptoms.[A18125] Alprazolam''s adverse effects are generally related to the sedation it can cause.[A18125] Alprazolam has been mixed with alcohol as a drug of abuse to potentiate the sedative effects of the drug which may lead to coma and death.[A18125] Alprazolam was given FDA approval on October 16, 1981.[L6148]', '["N05BA12"]', '[]');
