INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (16219, 'Champix 0.5mg & 1 mg 24 f.c. tab.(starter pack)', 'شامبيكس 0.5مجم و 1مجم 24 قرص - علبة البدء', '250', '', 'Smoking cessation.nicotinic agonist', 'Varenicline', 'Pfizer', 'Tab', 'أقراص', '0.5mg', '2', '', 'about varenicline tartrate nicotinic receptor partial agonist mechanism of action of varenicline tartrate varenicline is a partial agonist of the ?4?2 subtype of the nicotinic acetylcholine receptor. in addition it acts on ?3?4 and weakly on ?3?2 and ?6-c', '5013457019643', '', 1782, '2022-12-03', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Varenicline is a prescription medication used to treat smoking addiction. This medication is the first approved nicotinic receptor partial agonist. Specifically, varenicline is a partial agonist of the alpha4/beta2 subtype of the nicotinic acetylcholine receptor. In addition it acts on alpha3/beta4 and weakly on alpha3beta2 and alpha6-containing receptors. A full agonism was displayed on alpha7-receptors.

On March 9, 2015, the U.S. Food and Drug Administration warned that Varenicline, in the form of Pfizer Inc''s quit-smoking drug, Chantix, has been associated with seizures and that some patients who drink while taking the drug may become aggressive or black out. Pfizer is conducting an additional safety study of the drug, results of which are  expected in late 2015. The FDA said it is keeping the black box in place at least until the results of the trial are announced.', '["N07BA03"]', '[]');
