INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (17046, 'D3 candy children 30 pieces', 'دي3 كاندي اطفال 20 قطعة', '59', '', 'Vitamin d', 'Cholecalciferol(vitamin d3)', 'El delta for sweets and food industries > devartlab pharma', 'Piece', 'قطعة', '', '1', '', '', '', '', 1015, '2022-07-07', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Vitamin D, in general, is a secosteroid generated in the skin when 7-dehydrocholesterol located there interacts with ultraviolet irradiation - like that commonly found in sunlight [L5689]. Both the endogenous form of vitamin D (that results from 7-dehydrocholesterol transformation), vitamin D3 (cholecalciferol), and the plant-derived form, vitamin D2 (ergocalciferol), are considered the main forms of vitamin d and are found in various types of food for daily intake [L5689]. Structurally, ergocalciferol differs from cholecalciferol in that it possesses a double bond between C22 and C23 and has an additional methyl group at C24 [L5689]. Finally, ergocalciferol is pharmacologically less potent than cholecalciferol, which makes vitamin D3 the preferred agent for medical use [L5689].
