INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (17049, 'Azathioprine-evapharma 100 mg 30 f.c.tabs.', 'ازاثيوبرين ايفا فارما 100 مجم 30 قرص', '153', '', 'Immunosuppressant', 'Azathioprine', 'One pharma medics', 'Tab', 'أقراص', '100 mg', '3', '', '', '', '', 1107, '2022-08-01', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Azathioprine is a prodrug of 6-mercaptopurine, first synthesized in 1956 by Gertrude Elion, William Lange, and George Hitchings in an attempt to produce a derivative of 6-mercaptopurine with a better therapeutic index.[A189678,A189687,A189690] Azathioprine is used to treat inflammatory conditions like rheumatoid arthritis and as an immunosuppressant in the prevention of renal transplant rejection.[L11214]
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (17050, 'Daclahepex 60 mg 28 f.c. tabs.', 'داكلاهيبيكس 60 مجم 28 قرص', '120', '', 'Antiviral', 'Daclatasvir', 'Global napi pharmaceuticals > global pharmaceutical industries', 'Tab', 'أقراص', '60 mg', '4', '', '*daclatasvir is a drug for the treatment of hepatitis c (hcv). *daclatasvir inhibits the hcv nonstructural protein ns5a. recent research suggests that it targets two steps of the viral replication process enabling rapid decline of hcv rna. *daclatasvir ha', '', '', 1169, '2022-07-15', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Daclatasvir is a direct-acting antiviral agent against Hepatitis C Virus (HCV) used for the treatment of chronic HCV genotype 1 and 3 infection. It is marketed under the name DAKLINZA and is contained in daily oral tablets as the hydrochloride salt form . Hepatitis C is an infectious liver disease caused by infection with Hepatitis C Virus (HCV). HCV is a single-stranded RNA virus that is categorized into nine distinct genotypes, with genotype 1 being the most common in the United States, and affecting 72% of all chronic HCV patients [L852]. Daclatasvir was the first drug with demonstrated safety and therapeutic efficacy in treating HCV genotype 3 without the need for co-administration of interferon or [DB00811]. It exerts its antiviral action by preventing RNA replication and virion assembly via binding to NS5A, a nonstructural phosphoprotein encoded by HCV. Binding to the N-terminus of the D1 domain of NS5A prevents its interaction with host cell proteins and membranes required for virion replication complex assembly. Daclatasvir is shown to target both the cis- and trans-acting functions of NS5A and disrupts the function of new HCV replication complexes by modulating the NS5A phosphorylation status [A19640]. The most common critical NS5A amino acid substitutions that led to reduced susceptibility to daclatasvir therapy occured at position Q30 (Q30H/K/R) and M28 in genotype 1a patients and Y93H in genotype 3 patients. 
