INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (17062, 'Dajuanofen 200 mg 20 caps', 'داجوانوفين 200 مجم 20 كبسولة', '4', '', 'Nsaid.propionic acid derivatives', 'Ibuprofen', 'Copad pharma', 'Cap', 'كبسولة', '200 mg', '2', 'مسكن وخافض للحرارة ومضاد للالتهابات', 'about ibuprofen nsaid a propionic acid derivative analgesic and anti-inflammatory. mechanism of action of ibuprofen ibuprofen has analgesic anti-inflammatory and antipyretic action. it acts by inhibiting prostaglandin (pgs) synthesis and their release at', '', '', 1092, '2022-07-08', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Ibuprofen is a non-steroidal anti-inflammatory drug (NSAID) derived from propionic acid and it is considered the first of the propionics.[A39074] The formula of ibuprofen is 2-(4-isobutylphenyl) propionic acid and its initial development was in 1960 while researching for a safer alternative for aspirin.[A39075] Ibuprofen was finally patented in 1961 and this drug was first launched against rheumatoid arthritis in the UK in 1969 and USA in 1974. It was the first available over-the-counter NSAID.[A39076]
