INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (19558, 'Gaptin 250mg/5ml oral solution 120 ml', 'جابتن 250مجم/5مل شراب 120 مل', '45', '', 'Anti-epileptic.gaba analogs', 'Gabapentin', 'Delta pharma', 'Solution', 'محلول', '250mg', '1', 'علاج الصرع -- يخفف الالام العصبية', 'about gabapentin a gaba analogue anticonvulsant mood stabilizer. mechanism of action of gabapentin gabapentin is a gaba derivative and it bounds to lipophilic molecule. the mechanism of action of gabapentin is not exactly known. it crosses blood brain bar', '', '', 1291, '2022-08-16', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Gabapentin is a structural analogue of the inhibitory neurotransmitter gamma-aminobutyric acid ([GABA]) that was first approved for use in the United States in 1993.[L8717] It was originally developed as a novel anti-epileptic for the treatment of certain types of seizures[A186277,A186143] - today it is also widely used to treat neuropathic pain.[A14097,A186179] Gabapentin has some stark advantages as compared with other anti-epileptics, such as a relatively benign adverse effect profile, wide therapeutic index, and lack of appreciable metabolism making it unlikely to participate in pharmacokinetic drug interactions.[A186143,A185981,L8717]. It is structurally and functionally related to another GABA derivative, [pregabalin].', '["N03AX12"]', '[]');
