INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (20840, 'Inletair 10mg 10 f.c.tab', 'انليتير 10مجم 10 اقراص', '47', '38', 'Asthma.anti-inflammatory.leukotriene antagonist', 'Montelukast', 'Organopharma', 'Tab', 'أقراص', '10mg', '1', '', '', '6224000908003', '', 1085, '2024-05-22', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Montelukast was first approved for clinical use by the US FDA in 1998 as Merck''s brand name Singulair.[L6301] The medication is a member of the leukotriene receptor antagonist (LTRA) category of drugs.[L6301,L6304,L6307,L6310,L6325,L6328,L6331] Although capable of demonstrating effectiveness, the use of such LTRAs like montelukast is typically in addition to or complementary with the use of inhaled corticosteroids or other agents in asthma step therapy.[A178645] Regardless, in 2008-2009, there were FDA-led investigations into the possibility of montelukast to elicit neuropsychiatric effects like agitation, hallucinations, suicidal behaviour, and others in individuals who used the medication.[A178651] And although these kinds of effects are currently included in the official prescribing information for montelukast,[L6301,L6304,L6307,L6310,L6325,L6328,L6331] the drug still sees extensive use worldwide via millions of prescriptions annually and has since become available as a generic and as a brand name product.', '["R03DC03", "R03DC53"]', '[]');
