INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (21220, 'Kadomet 250mg 30 tab', 'كادوميت 250 مجم 30 قرص', '20', '', 'Antihypertensives.centeral alpha 2 agonist', 'Methyldopa', 'Kahira', 'Tab', 'أقراص', '250mg', '3', '', 'about methyldopa centrally acting alpha-2 agonist a phenylalanine derivative antihypertensive (pregnancy-induced hypertension). mechanism of action of methyldopa methyl dopa gets metabolized in the adrenergic neurons to alpha methyl nor adrenaline which a', '6221068924525', '', 729, '2022-12-06', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Methyldopa, or α-methyldopa, is a centrally acting sympatholytic agent and an antihypertensive agent.[A231784] It is an analog of DOPA (3,4‐hydroxyphenylanine), and it is a prodrug, meaning that the drug requires biotransformation to an active metabolite for therapeutic effects. Methyldopa works by binding to alpha(α)-2 adrenergic receptors as an agonist, leading to the inhibition of adrenergic neuronal outflow and reduction of vasoconstrictor adrenergic signals.[A1499] Methyldopa exists in two isomers D-α-methyldopa and L-α-methyldopa, which is the active form.[A232224]

First introduced in 1960 as an antihypertensive agent, methyldopa was considered to be useful in certain patient populations, such as pregnant women and patients with renal insufficiency. Since then, methyldopa was largely replaced by newer, better-tolerated antihypertensive agents;[A231784] however, it is still used as monotherapy [L32614] or in combination with [hydrochlorothiazide].[L32619] Methyldopa is also available as intravenous injection, which is used to manage hypertension when oral therapy is unfeasible and to treat hypertensive crisis.[L32624]', '["C02AB01"]', '[]');
