INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (22078, 'Lowparin 60mg/0.6ml soln. for s.c injection (n/a)', 'لوبارين 60مجم/0.6مل محلول للحقن تحت الجلد', '16', '', 'Anticoagulant.low mwt heparin', 'Enoxaparin sodium', 'El nile. > epci', 'Injection', 'حقن', '60mg', '1', '', 'about enoxaparin sodium a low molecular weight heparin anticoagulant. mechanism of action of enoxaparin sodium enoxaparin sodium is a low molecular weight heparin that accelerates formation of antithrombin iii- thrombin complex and deactivates thrombin an', '', '', 1244, '2022-06-26', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Enoxaparin is a common low-molecular-weight heparin (LMWH) used in the prevention and management of various thromboembolic disorders. Initially approved by the FDA in 1993, it is administered by a subcutaneous or intravenous injection and marketed by several pharmaceutical companies.[L31393] Enoxaparin markedly reduces the incidence of venous thromboembolism in hospitalized patients when compared to unfractionated [heparin], without increasing the risk of serious bleeding.[A228178,A228313]', '["B01AB05"]', '[]');
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (22079, 'Low-sterol 10mg 20 f.c. tab.', 'لو استيرول 10مجم 20 قرص', '64', '', 'Antihyperlipidemic.statins', 'Simvastatin', 'T3a pharma', 'Tab', 'أقراص', '10mg', '2', '', '', '6221014000839', '', 820, '2022-07-15', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Simvastatin, also known as the brand name product Zocor, is a lipid-lowering drug derived synthetically from a fermentation product of _Aspergillus terreus_. It belongs to the statin class of medications, which are used to lower the risk of cardiovascular disease and manage abnormal lipid levels by inhibiting the endogenous production of cholesterol in the liver. More specifically, statin medications competitively inhibit the enzyme hydroxymethylglutaryl-coenzyme A (HMG-CoA) Reductase,[A181421] which catalyzes the conversion of HMG-CoA to mevalonic acid and is the third step in a sequence of metabolic reactions involved in the production of several compounds involved in lipid metabolism and transport including cholesterol, low-density lipoprotein (LDL) (sometimes referred to as "bad cholesterol"), and very low-density lipoprotein (VLDL). Prescribing of statin medications is considered standard practice following any cardiovascular events and for people with a moderate to high risk of development of CVD, such as those with Type 2 Diabetes. The clear evidence of the benefit of statin use coupled with very minimal side effects or long term effects has resulted in this class becoming one of the most widely prescribed medications in North America.[A181087, A181406]
