INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (22203, 'Magicrelide 0.5 mg 10 caps.', 'ماجيسريليد 0.5مجم 10كبسول', '50', '', 'Thrombocythemia', 'Anagrelide', 'Medizen pharmaceutical industries > magic pharma-egypt', 'Cap', 'كبسولة', '0.5 mg', '1', '', 'indication: for the treatment of patients with thrombocythemia secondary to myeloproliferative disorders to reduce the elevated platelet count and the risk of thrombosis and to ameliorate associated symptoms including thrombo-hemorrhagic events. dosage: a', '', '', 1118, '2022-07-13', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Anagrelide is a platelet-reducing agent used to lower dangerously elevated platelet levels (i.e. to treat thrombocythemia) in patients with myeloproliferative neoplasms.[L14153] It is an oral imidazoquinazoline that was first approved for use in the US in 1997.[A214274] It appears to carry a better response rate than other thrombocythemia treatments (e.g. [busulfan], [hydroxyurea]) and may be better tolerated.[A214274]', '["L01XX35"]', '[]');
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (22204, 'Magitamsulosin 0.4 mg 30 extended rel. caps.', 'ماجيتامسولوسين 0.4مجم 30 كبسولة ممتدة المفعول', '54', '', 'Urination-difficulty.alpha blocker', 'Tamsulosin', 'Medizen pharmaceutical industries > magic pharma-egypt', 'Cap', 'كبسولة', '0.4 mg', '3', 'يساعد علي التبول في حالات الاحتباس البولي __ يقلل الالام الناتجة من احتباس البول', 'about tamsulosin selective alpha1a adrenergic antagonist for benign prostatic hyperplasia. mechanism of action of tamsulosin tamsulosin is adrenergic alpha receptor antagonist. it inhibits alpha 1a and alpha 1d subtype receptors. tamsulosin selectively in', '', '', 2487, '2023-02-14', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Tamsulosin is a selective alpha-1A and alpha-1B adrenoceptor antagonist that exerts its greatest effect in the prostate and bladder, where these receptors are most common.[Label] It is indicated for the treatment of signs and symptoms of benign prostatic hypertrophy.[Label] Antagonism of these receptors leads to relaxation of smooth muscle in the prostate and detrusor muscles in the bladder, allowing for better urinary flow.[Label] Other alpha-1 adrenoceptor antagonists developed in the 1980s were less selective and more likely to act on the smooth muscle of blood vessels, resulting in hypotension.[A178351]
