INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (22273, 'Mantine 10mg 30 f.c. tab.', 'مانتين 10مجم 30 قرص', '180', '120', 'Dopamine d2 agonist', 'Memantine', 'Adwia', 'Tab', 'أقراص', '10mg', '3', '', 'about memantine n-methyl-d-aspartate receptor antagonist dopamine d2 agonist antidementia agent in alzheimer`s disease. mechanism of action of memantine memantine in an uncompetitive (low to moderate affinity to) nmda (n-methyl-d-aspartate) receptor antag', '6223002978182', '', 1608, '2025-08-23', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Initially approved by the FDA in 2013, memantine is an N-methyl-D-aspartate (NMDA) receptor antagonist used in the management of Alzheimer''s Disease (AD).  It is different from many other Alzheimer''s Disease medications, as it works by a different mechanism than the cholinesterase enzyme inhibitors normally employed in the management of Alzheimer''s disease [A1639].  Memantine blocks the effects of glutamate, a neurotransmitter in the brain that leads to neuronal excitability and excessive stimulation in Alzheimer''s Disease [T556].
