INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (29689, 'Wondril 10 h.g. caps.', 'وندريل 10 كبسول', '6', '', 'Laxative', 'Docusate sodium', 'Kahira', 'Cap', 'كبسولة', '', '1', '', 'docusate is a stool softener. it works by increasing the amount of water the stool absorbs in the gut making the stool softer and easier to pass. side effects stomach pain diarrhea or cramping may occur. irritated throat (with liquid or syrup forms) may a', '', '', 1311, '2022-07-12', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Docusate, or dioctyl sulfosuccinate, is a stool softener indicated for the treatment of constipation[A32201]. Docusate acts by increasing the amount of water the stool absorbs in the gut, making the stool softer and easier to pass [L1801]. Docusate can be orally or rectally administered. Docusate is on the World Health Organization''s List of Essential Medicines[L5915]. However the effectiveness of docusate in treating constipation remains unclear, as several studies report docusate to be no more effective than placebo for increasing the frequency of stool or stool softening [A32200,A32202,A176987]. Recently there has been pressure to stop prescribing docusate as it has been identified as an ineffective medicine[A176972,A176987,L5912]. Additionally, it does not appear to lessen symptoms associated with constipation such as abdominal cramps. Still docusate is available in over-the-counter products as a common laxative.', '[]', '[]');
INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (29690, 'Wormine 40mg/5ml syrup 15ml', 'ورمين 40مجم/5مل شراب 15مل', '4', '', 'Anthelmintic', 'Levamisole', 'Unipharma co.', 'Syrup', 'شراب', '40mg', '1', '', 'about levamisole synthetic imidazothiazole derivative biological response modifier anthelmintic antineoplastic adjunct. mechanism of action of levamisole levamisole is the levo isomer of tetramisole which is used as an anthelminthic agent. it exerts its a', '', '', 872, '2022-07-20', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Levamisole is an antihelminthic drug that was commonly used for the treatment of parasitic, viral, and bacterial infections. [A178117] It was manufactured by _Janssen_ and first used in 1969 as an agent to treat worm infestations[A178123] Levamisole was approved by the FDA in 1990 as an adjuvant treatment for colon cancer.[L6232] Prior to this, levamisole was used as an antirheumatic therapy in the 1970s and 1980s for patients with rheumatoid arthritis.[A178123] 

Because of its immunomodulatory effects, this drug has been studied in the treatment of various immune-mediated diseases, with some studies showing positive results. [A178117] This drug has also been used in combination with other drugs for the treatment of various cancers. [A178117, A178123] 

Levamisole was withdrawn from the American market in 2000 due to its ability to cause serious adverse effects, including agranulocytosis. [A178123] Interestingly, levamisole has been found as an adulterant in cocaine and can lead to a variety of adverse effects in individuals using this drug.[A178120]', '["P02CE01"]', '[]');
