INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (30662, 'Hyrimoz 40mg/0.8ml 2 pref. syringes', 'هيريموز 40مجم/0.8مل 2 سرنجة معبأة', '8,435', '6750', 'Tnf-inhibitor', 'Adalimumab', 'Sandoz > novartis pharma sandoz division', 'Syringe', 'حقنة', '40mg', '2', 'يعالج الالتهابات المتوسطة إلى الشديدة مثل: التهاب المفاصل الروماتيدي. التهاب المفاصل مع الصدفية. التهاب الفقار المقسط.', 'adalimumab is a medication used to treat rheumatoid arthritis psoriatic arthritis ankylosing spondylitis crohn s disease ulcerative colitis chronic psoriasis hidradenitis suppurativa and juvenile idiopathic arthritis. in rheumatoid arthritis adalimumab ha', '', '', 12236, '2024-03-16', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Adalimumab is a subcutaneously administered biological disease modifier for the treatment of rheumatoid arthritis and other chronic debilitating diseases mediated by tumor necrosis factor.[A39984,A39999] It was originally launched by Abbvie in the U.S. and approved in 2002 by the FDA.[A39983] This drug is frequently known as _Humira_. It is produced by recombinant DNA technology using a mammalian cell expression system. This drug is available in a prefilled syringe form and convenient pen form for subcutaneous self-administered doses.[A39983] A new biosimilar to adalimumab, named _adalimumab-adaz_, was approved by the FDA on October 31, 2018. This biosimilar is known as _Hyrimoz_, and is a trademark of Novartis AG.[L4799] Other biosimilars of adalimumab include adalimumab-fkjp, which was approved in July 2022,[L42495] and HADLIMA (adalimumab-bwwd), which was approved in August 2022.[L42935]', '["L04AB04"]', '[]');
