INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (30844, 'Lokelma 10 gm 3 sachets', 'لوكيلما 10جم 3 اكياس', '883', '580', 'Potassium binders', 'Sodium zirconium cyclosilicate', 'Astrazeneca > astrazeneca scientific office', 'Sachet', 'أكياس', '10 gm', '3', 'يستخدم في علاج فرط بوتاسيوم الدم (ارتفاع نسبة البوتاسيوم في الدم).', 'sodium zirconium cyclosilicate is an insoluble non-absorbed non-polymer inorganic material designed to bind potassium ions with proven selectivity to treat hyperkalaemia. this is a potentially serious condition caused by raised potassium levels in the blo', '', '', 6492, '2024-11-02', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Sodium zirconium cyclosilicate is approved as the trade product Lokelma developed by AstraZeneca - an insoluble, non-absorbed sodium zirconium silicate, formulated as a powder for oral suspension that acts as a highly selective potassium removing agent.[L2933] It is administered orally and is odorless, tasteless, and stable at room temperature.[L2933] Approval of the medication is supported by data from three double-blind, placebo-controlled trials and two open-label trials which showed that the onset of action was approximately 1.0 hour and the median time to achieving normal potassium levels in the blood was 2.2 hours, with 92% of patients achieving normal potassium levels within 48 hours following administration.[L2933] The treatment effect was maintained for up to 12 months.[L2933]', '["V03AE10"]', '[]');
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (30845, 'Lokelma 5 gm 30 sachets', 'لوكيلما 5جم 30 كيس', '8,027', '5917', 'Potassium binders', 'Sodium zirconium cyclosilicate', 'Astrazeneca > astrazeneca scientific office', 'Sachet', 'أكياس', '5 gm', '1', '', 'sodium zirconium cyclosilicate is an insoluble non-absorbed non-polymer inorganic material designed to bind potassium ions with proven selectivity to treat hyperkalaemia. this is a potentially serious condition caused by raised potassium levels in the blo', '', '', 10039, '2024-11-01', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Sodium zirconium cyclosilicate is approved as the trade product Lokelma developed by AstraZeneca - an insoluble, non-absorbed sodium zirconium silicate, formulated as a powder for oral suspension that acts as a highly selective potassium removing agent.[L2933] It is administered orally and is odorless, tasteless, and stable at room temperature.[L2933] Approval of the medication is supported by data from three double-blind, placebo-controlled trials and two open-label trials which showed that the onset of action was approximately 1.0 hour and the median time to achieving normal potassium levels in the blood was 2.2 hours, with 92% of patients achieving normal potassium levels within 48 hours following administration.[L2933] The treatment effect was maintained for up to 12 months.[L2933]', '["V03AE10"]', '[]');
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (30846, 'Lopranest 2 mg 20 orodispersible films', 'لوبرانيست 2مجم 20 فيلم', '90', '60', 'Antidiarrhoeal', 'Loperamide', 'Nerhadou international co.', 'Film', 'فيلم', '2 mg', '1', 'يستخدم لعلاج حالات الإسهال الحاد قصير الأمد. · تخفيف بعض أعراض القولون (الإسهال) لدى مرضى متلازمة القولون العصبي. · تقليل كمية البراز في بعض الحالات المرضية', 'about loperamide a synthetic piperidine derivative opioid antidiarrhoeal analgesic. mechanism of action of loperamide loperamide is a mu receptor agonist. it inhibits the release of acetylcholine on myenteric plexus in the gastrointestinal tract. this res', '6224011153157', '', 2497, '2024-09-22', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Loperamide is an anti-diarrheal agent that is available as various over-the-counter products for treating diarrhea.[L42785] The drug was first synthesized in 1969 and used medically in 1976.[A251610] It is a highly lipophilic synthetic phenylpiperidine opioid [A251610] that is structurally similar to opiate receptor agonists such as [diphenoxylate] and [haloperidol].[A6249] Due to pharmacological properties, loperamide has been misused and abused to self-manage opioid withdrawal symptoms and to induce euphoria.[A251610, A251625] However, loperamide is associated with a risk for experiencing a range of adverse effects, often life-threatening, if taking for non-therapeutic reasons or at doses higher than the recommended dose.[L42790]', '["A07DA53", "A07DA03"]', '[]');
