INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (31061, 'Pacifial 20mg 21 dela. rel. caps.', 'باسيفيال 20مجم 21 كبسولة', '44', '', 'Peptic ulcer.proton pump inhibitor', 'Esomeprazole', 'Sigma', 'Cap', 'كبسولة', '20mg', '3', 'علاج قرحة المعدة و اعراض ارتجاع المرىء و علاج الهيلكوباكتر بيلورى.', 'about esomeprazole a proton pump inhibitor substituted benzimidazole antiulcer antisecretory drug. mechanism of action of esomeprazole esomeprazole is the s enantiomer of omeprazole and is a proton pump inhibitor. it is a prodrug. after administration it', '', '', 5948, '2023-06-08', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Esomeprazole, sold under the brand name Nexium, is a proton pump inhibitor (PPI) medication used for the management of gastroesophageal reflux disease (GERD), for gastric protection to prevent recurrence of stomach ulcers or gastric damage from chronic use of NSAIDs, and for the treatment of pathological hypersecretory conditions including Zollinger-Ellison (ZE) Syndrome. It can also be found in quadruple regimens for the treatment of _H. pylori_ infections along with other antibiotics including [DB01060], [DB01211], and [DB00916], for example.[A177271, F4498] Its efficacy is considered similar to other medications within the PPI class including [DB00338], [DB00213], [DB00448], [DB05351], and [DB01129]. Esomeprazole is the s-isomer of [DB00338], which is a racemate of the S- and R-enantiomer. Esomeprazole has been shown to inhibit acid secretion to a similar extent as [DB00338], without any significant differences between the two compounds _in vitro_. 
