INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (31769, 'G.xir (d310000iu) 30 tablets', 'جي.اكسير (د3- 10000 وحدة دولية) 30 قرص', '179', '159', 'Dietary supplement', 'Cholecalciferol', 'Dawa egypt international > elixir pharma george group', 'Tablet', 'أقراص', '310000iu', '3', 'مكمل غذائى لعلاج حالات نقص فيتامين د ,هشاشة العظام , نقص الكالسيوم و الكساح.', '', '', '', 7158, '2025-09-26', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Vitamin D, in general, is a secosteroid generated in the skin when 7-dehydrocholesterol located there interacts with ultraviolet irradiation - like that commonly found in sunlight [L5689]. Both the endogenous form of vitamin D (that results from 7-dehydrocholesterol transformation), vitamin D3 (cholecalciferol), and the plant-derived form, vitamin D2 (ergocalciferol), are considered the main forms of vitamin d and are found in various types of food for daily intake [L5689]. Structurally, ergocalciferol differs from cholecalciferol in that it possesses a double bond between C22 and C23 and has an additional methyl group at C24 [L5689]. Finally, ergocalciferol is pharmacologically less potent than cholecalciferol, which makes vitamin D3 the preferred agent for medical use [L5689].
