INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (31915, 'Venclexta 10, 50, 100 mg 42 tabs.', 'فينكليكستا 100 50 10 مجم 42 قرص', '23,323', '15900', 'Antitumor', 'Venetoclax', 'Abbvie ltd. > abbvie scientific office', 'Tab', 'أقراص', '100 mg', '3', '', '', '', '', 659, '2024-06-25', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Venetoclax is a BCL-2 inhibitor that was initially approved by the FDA in April 2016 [FDA label]. Proteins in the B cell CLL/lymphoma 2 (BCL-2) family are important regulators of the apoptotic (programmed cell death) process [A18565], [A18566].  Venetoclax is used to treat chronic lymphocytic leukemia (CLL) and certain types of small lymphocytic lymphoma [FDA label]. CLL is the most prevalent leukemia diagnosed in Western countries [A40022]. Venetoclax was developed through reverse engineering of the BCL-2 protein family inhibitor, navitoclax [A40022]. Venetoclax is approximately 10 times more potent than navitoclax with regard to induction of apoptosis in CLL cells [A40022]. A new indication was approved in 2018 for the treatment patients with chronic lymphocytic leukemia (CLL) or small lymphocytic lymphoma (SLL), with or without 17p deletion, who have received at least one prior therapy [FDA label].  Previously, this drug was indicated only for patients with 17p gene deletion [F2130].', '["L01XX52"]', '[]');
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (31916, 'Rinvoq 15 mg 30 prolonger rel. tabs.', 'رينفوك 15مجم 30 قرص', '23,658', '17649', '', 'Upadacitinib', 'Abbvie ltd. > abbvie scientific office', 'Tab', 'أقراص', '15 mg', '3', '', 'rinvoq is a janus kinase (jak) inhibitor indicated for the treatment of: -rheumatoid arthritis. -psoriatic arthritis. -atopic dermatitis. -ulcerative colitis. -ankylosing spondylitis.', '300742306300', '', 27645, '2024-11-11', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Upadacitinib is an oral Janus kinase (JAK)1-selective inhibitor and a disease-modifying antirheumatic drug (DMARD) used in the treatment of rheumatoid arthritis to slow down disease progression. Rheumatoid arthritis is a chronic autoimmune inflammatory disease affecting the peripheral joints. It is characterized by synovial inflammation and hyperplasia, autoantibody production, cartilage damage and bone destruction, leading to co-morbidities.[A189165] Despite a variety of therapeutic agents available for treatment, up to 40% of the patients do not respond to current therapies, including biological therapies.[A189171] The etiology of the disease is mostly unknown; however, the role of JAK as a driver of immune-mediated conditions was discovered, leading to the use of JAK as therapeutic targets for rheumatoid arthritis.[A189168] To reduce dose-related toxicity (as seen with some pan-JAK inhibitors) without significantly affecting efficacy, more selective JAK1 inhibitors, upadacitinib and [filgotinib], were developed.[A189165]
