INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (31974, 'Drospinetta 4 mg 28 f.c.tabs.', 'دروسبينيتا 4مجم 28 قرص', '112', '84.5', 'Contraceptives', 'Drospirenone', 'Marcyrl', 'Tab', 'أقراص', '4 mg', '1', 'اقراص منع الحمل لتنظيم الاسرة', 'it is a progestin indicated for use by females of reproductive potential to prevent pregnancy. it has a 24+4 dosing regimen: 24 active hormone pills + 4 placebo pills.', '6223003572020', '', 31570, '2025-03-18', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Drospirenone is a synthetic progestin commonly found in the popular oral contraceptive, Yaz in combination with [Ethinyl estradiol].[L7973] Most recently, it was approved by both Health Canada and the FDA in combination with [Estetrol] as an oral contraceptive therapy.[L33199,L33174]  Aside from its contraceptive effects, drospirenone is used with estrogens to control acne and premenstrual dysphoric disorder (PMDD).
INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (31975, 'Tulangodrop 400 i.u./drop oral drops 30 ml', 'تولانجودروب 400 وحدة دولية 30 مل', '49', '39', 'Vitamin d', 'Cholecalciferol(vitamin d3)', 'Praxo pharm', 'Drops', 'نقط', '30 ml', '1', 'مكمل غذائى لدعم صحة العظام والجهاز المناعى', 'doage: 1 drop per day (=400 iu vitamin d) indication for the treatment of vitamin d deficiency or insufficiency refractory rickets (vitamin d resistant rickets) familial hypophosphatemia and hypoparathyroidism and in the management of hypocalcemia and ren', '', '', 909, '2024-03-22', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Vitamin D, in general, is a secosteroid generated in the skin when 7-dehydrocholesterol located there interacts with ultraviolet irradiation - like that commonly found in sunlight [L5689]. Both the endogenous form of vitamin D (that results from 7-dehydrocholesterol transformation), vitamin D3 (cholecalciferol), and the plant-derived form, vitamin D2 (ergocalciferol), are considered the main forms of vitamin d and are found in various types of food for daily intake [L5689]. Structurally, ergocalciferol differs from cholecalciferol in that it possesses a double bond between C22 and C23 and has an additional methyl group at C24 [L5689]. Finally, ergocalciferol is pharmacologically less potent than cholecalciferol, which makes vitamin D3 the preferred agent for medical use [L5689].
