INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (32091, 'Devart b12 1000 mcg 30 sublingual tabs', 'ديفارت ب12 - 1000مكجم 30 قرص تحت اللسان', '80', '', 'Vitamin b12', 'Cyanocobalamin', 'Devartlab > bioera', 'Tab', 'أقراص', '1000 mcg', '1', 'مكمل غذائى لدعم الجهاز العصبى وامداد الجسم بالطاقة', '', '6223006935839', '', 1530, '2023-04-21', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Cyanocobalamin (commonly known as Vitamin B12) is a highly complex, essential vitamin, owing its name to the fact that it contains the mineral, cobalt. This vitamin is produced naturally by bacteria [A175276], and is necessary for DNA synthesis and cellular energy production. Vitamin B12 has many forms, including the cyano-, methyl-, deoxyadenosyl- and hydroxy-cobalamin forms. The _cyano_ form, is the most widely used form in supplements and prescription drugs [A175255], [FDA label].  Several pharmaceutical forms of cyanocobalamin have been developed, including the tablet, injection, and nasal spray forms [FDA label], [L5542], [L5545].  This drug was initially approved by the FDA in 1942 [FDA label].', '["B03BA01", "B03AE01", "B03BA51"]', '[]');
