INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (32221, 'Limitless ossofortin d3 (5000 i.u.) 30 tabs.', 'ليمتلس اوسوفورتين د3 5000 وحدة دولية 30 قرص', '115', '115', 'Vitamin d', 'Cholecalciferol(vitamin d3)', 'Eva pharma', 'Tab', 'أقراص', '', '3', 'مكمل غذائى يدعم نمو العظام ويقى من الكساح وهشاشة العظام', '', '6223012480460', 'http://q-r.to/Limitless-Ossofortin-D3-5000-IU', 20999, '2025-05-22', 0, NULL, NULL, NULL, NULL, 0, 0, 0, 'Vitamin D, in general, is a secosteroid generated in the skin when 7-dehydrocholesterol located there interacts with ultraviolet irradiation - like that commonly found in sunlight [L5689]. Both the endogenous form of vitamin D (that results from 7-dehydrocholesterol transformation), vitamin D3 (cholecalciferol), and the plant-derived form, vitamin D2 (ergocalciferol), are considered the main forms of vitamin d and are found in various types of food for daily intake [L5689]. Structurally, ergocalciferol differs from cholecalciferol in that it possesses a double bond between C22 and C23 and has an additional methyl group at C24 [L5689]. Finally, ergocalciferol is pharmacologically less potent than cholecalciferol, which makes vitamin D3 the preferred agent for medical use [L5689].

Appropriate levels of vitamin D must be upheld in the body in order to maintain calcium and phosphorus levels in a healthy physiologic range to sustain a variety of metabolic functions, transcription regulation, and bone metabolism [A223, L5689, L1782, L5771, F4027, F4042, F4048]. However, studies are also ongoing to determine whether or not cholecalciferol may also play certain roles in cancer, autoimmune disorders, cardiovascular disease, and other medical conditions that may be associated with vitamin D deficiency [L5689].', '["M05BB09", "M05BX53", "M05BB07", "M05BB08", "A11CC55", "M05BB05", "A11CC05", "M05BB03", "M05BB04"]', '[]');
