INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (32234, 'Olazorix 5 mg 30 f.c.tabs.', 'اولازوريكس 5مجم 30 قرص', '123', '90', 'Psychiatric.antipsychotics', 'Olanzapine', 'Mash premiere', 'Tab', 'أقراص', '5 mg', '3', 'علاج اعراض الانفصام', '', '', '', 1776, '2025-01-22', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Olanzapine is a thienobenzodiazepine classified as an atypical or second-generation antipsychotic agent.[A176996] The second-generation antipsychotics were introduced in the 90s and quickly gained traction due to their impressive efficacy, reduced risk for extrapyramidal side effects and reduced susceptibility to drug-drug interactions.[A177011] Olanzapine very closely resembles [clozapine] and only differs by two additional methyl groups and the absence of a chloride moiety.[T554] It was discovered by scientists at Eli Lilly and approved to be marketed in the US in 1996.[T548]', '["N05AH03"]', '[]');
