INSERT OR REPLACE INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (32696, 'curepro xr 0.4 mg 10 caps.', 'كيور برو اكس ار 0.4مجم 10 كبسولات', '35', '25', '', 'tamsulosin', 'khufu pharma', 'capsule', '', '0.4 mg', '1', '', 'about tamsulosin selective alpha1a adrenergic antagonist for benign prostatic hyperplasia. mechanism of action of tamsulosin tamsulosin is adrenergic alpha receptor antagonist. it inhibits alpha 1a and alpha 1d subtype receptors. tamsulosin selectively in', '6224009652846', '', 650, '2025-12-17', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Tamsulosin is a selective alpha-1A and alpha-1B adrenoceptor antagonist that exerts its greatest effect in the prostate and bladder, where these receptors are most common.[Label] It is indicated for the treatment of signs and symptoms of benign prostatic hypertrophy.[Label] Antagonism of these receptors leads to relaxation of smooth muscle in the prostate and detrusor muscles in the bladder, allowing for better urinary flow.[Label] Other alpha-1 adrenoceptor antagonists developed in the 1980s were less selective and more likely to act on the smooth muscle of blood vessels, resulting in hypotension.[A178351]

