INSERT INTO drugs (id, trade_name, arabic_name, price, old_price, category, active, company, dosage_form, dosage_form_ar, concentration, unit, usage, pharmacology, barcode, qr_code, visits, last_price_update, updated_at, indication, mechanism_of_action, pharmacodynamics, data_source_pharmacology, has_drug_interaction, has_food_interaction, has_disease_interaction, description, atc_codes, external_links) VALUES (33919, 'Zaltrap 25mg/ml conc. vial for inf. 8 ml', 'زالتراب 25مجم /مل فيال 8 مل', '10,088', '9680', 'Antineoplastic', 'Aflibercept', 'Sanofi', 'Vial', 'فيال', '25mg', '1', '', '', '', '', 525, '2024-07-21', 0, NULL, NULL, NULL, NULL, 1, 0, 0, 'Aflibercept is a recombinant protein composed of the binding domains of two human vascular endothelial growth factor (VEGF) receptors fused with the Fc region of human immunoglobulin gamma 1 (IgG1). Structurally, Aflibercept is a dimeric glycoprotein with a protein molecular weight of 96.9 kilo Daltons (kDa). It contains approximately 15% glycosylation to give a total molecular weight of 115 kDa. All five putative N-glycosylation sites on each polypeptide chain predicted by the primary sequence can be occupied with carbohydrate and exhibit some degree of chain heterogeneity, including heterogeneity in terminal sialic acid residues, except at the single unsialylated site associated with the Fc domain. Aflibercept, as an ophthalmic agent, is used in the treatment of macular edema following Central Retinal Vein Occlusion (CRVO) and neovascular Age-Related Macular Degeneration (AMD). Ziv-aflibercept, under the brand name Zaltrap, was developed as an injection for treatment of metastatic colorectal cancer. FDA approved in November 18, 2011 and EMA approved in November 2012.', '["S01LA05", "L01XX44"]', '[]');
