INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1746, 0, 'Pentazocine', 'Ethanol may potentiate the central nervous system (CNS) depressant effects of opioid analgesics.  Concomitant use may result in additive CNS depression and impairment of judgment, thinking, and psychomotor skills.  In more severe cases, hypotension, respiratory depression, profound sedation, coma, or even death may occur.', 'alcohol', 'Moderate', 'Concomitant use of opioid analgesics with ethanol should be avoided.', '', '', 'DDInter', 0);
INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1747, 0, 'Pentazocine', 'Smoking tobacco may decrease the plasma concentrations and effects of pentazocine by enhancing its metabolic clearance.', 'cigarette', 'Moderate', 'The possibility of reduced therapeutic effects of pentazocine should be considered in smokers.', '', '', 'DDInter', 0);
INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1748, 16931, 'Penicillamine', 'Food may interfere with the gastrointestinal absorption of penicillamine.', 'food', 'Moderate', 'Penicillamine should be administered on an empty stomach, at least one hour before or two hours after meals, and at least one hour apart from any other drug, food, or milk.  This permits maximum absorption and reduces the likelihood of inactivation by metal binding in the gastrointestinal tract.', '', '', 'DDInter', 0);
