INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1753, 0, 'Avatrombopag', 'Food reduces the variability in oral absorption and bioavailability of avatrombopag.  According to the product labeling, avatrombopag peak plasma concentration (Cmax) and systemic exposure (AUC) were not affected when administered with either a low-fat (500 calories; 3 g fat, 15 g proteins, 108 g carbohydrates) or high-fat (918 calories; 59 g fat, 39 g proteins, 59 g carbohydrates) meal.  However, the variability of avatrombopag exposure was reduced by 40% to 60% with food, and the time to reach Cmax was delayed by 0 to 2 hours relative to the fasted state.', 'high-fat foods', 'Moderate', 'To ensure consistent absorption and plasma drug levels, avatrombopag should be taken with food.', '', '', 'DDInter', 0);
