INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2198, 27526, 'Regorafenib', 'Depending on the amount of fat, food may enhance the oral bioavailability of both regorafenib and its active metabolites, M-2 and M-5.', 'high-fat foods', 'Moderate', 'To ensure optimal oral absorption, regorafenib should be administered with a low-fat breakfast that contains less than 30% fat.  Examples of a low-fat breakfast include:  2 slices of white toast with 1 tablespoon of low-fat margarine and 1 tablespoon of jelly, plus 8 ounces of skim milk (319 calories; 8.2 g fat); or 1 cup of cereal, 8 ounces of skim milk, 1 slice of toast with jam, apple juice, and 1 cup of coffee or tea (520 calories; 2 g fat).  Patients should be advised to avoid consuming grapefruit or grapefruit juice during treatment with regorafenib.', '', '', 'DDInter', 0);
