INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2200, 25848, 'Remdesivir', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury.  Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', 'alcohol', 'Moderate', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.  Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate.  Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline.  Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', '', '', 'DDInter', 0);
INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2201, 25849, 'Remdesivir', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury.  Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', 'alcohol', 'Moderate', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.  Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate.  Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline.  Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', '', '', 'DDInter', 0);
INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2202, 0, 'Regadenoson', 'Caffeine and other xanthine derivatives are nonspecific, competitive antagonists of adenosine receptors.  As such, they may interfere with the vasodilating effect of regadenoson, an adenosine receptor agonist.', 'coffee', 'Moderate', 'Patients should avoid consumption of caffeine-containing products for at least 12 hours prior to administration of regadenoson for myocardial perfusion imaging.', '', '', 'DDInter', 0);
INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2203, 28656, 'Remifentanil', 'Ethanol may potentiate the central nervous system (CNS) depressant effects of opioid analgesics.  Concomitant use may result in additive CNS depression and impairment of judgment, thinking, and psychomotor skills.  In more severe cases, hypotension, respiratory depression, profound sedation, coma, or even death may occur.', 'alcohol', 'Moderate', 'Concomitant use of opioid analgesics with ethanol should be avoided.', '', '', 'DDInter', 0);
