INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3249, 209, 'Valsartan', 'Moderate-to-high dietary intake of potassium, especially salt substitutes, may increase the risk of hyperkalemia in some patients who are using angiotensin II receptor blockers (ARBs).  ARBs can promote hyperkalemia through inhibition of angiotensin II-induced aldosterone secretion.', 'Food High in Potassium', 'Moderate', 'Patients should receive dietary counseling and be advised to not use potassium-containing salt substitutes or over-the-counter potassium supplements without consulting their physician.  If salt substitutes are used concurrently, regular monitoring of serum potassium levels is recommended.  Patients should also be advised to seek medical attention if they experience symptoms of hyperkalemia such as weakness, irregular heartbeat, confusion, tingling of the extremities, or feelings of heaviness in the legs.', '', '', 'DDInter', 0);
