INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3402, 12260, 'Voriconazole', 'Food reduces the oral absorption and bioavailability of voriconazole.  According to the product labeling, administration of multiple doses of voriconazole with high-fat meals decreased the mean peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) by 34% and 24%, respectively, when the drug is administered as a tablet, and by 58% and 37%, respectively, when administered as the oral suspension.', 'high-fat foods', 'Moderate', 'To ensure maximal oral absorption, voriconazole tablets and oral suspension should be taken at least one hour before or after a meal.', '', '', 'DDInter', 0);
