INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3413, 5162, 'Zinc acetate', 'The presence of certain foods (e.g., bread, bran, hard boiled eggs, coffee, and milk) may significantly delay the absorption of zinc acetate.  Fibres, phytates, and other substances in food prevent zinc from entering intestinal cells by binding with it.  Protein does not appear to interfere with its absorption.', 'bran', 'Moderate', 'Zinc acetate should preferably be taken on an empty stomach, at least one hour before or two to three hours after meals.  It may be taken with small amounts of protein such as meat.', '', '', 'DDInter', 0);
