INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3562, 0, 'Betrixaban', 'Food reduces the oral bioavailability of betrixaban.  When administered with a low-fat (900 calories; 20% fat) or high-fat (900 calories; 60% fat) meal, betrixaban peak plasma concentration (Cmax) and systemic exposure (AUC) decreased relative to administration in the fasting state by an average of 70% and 61%, respectively, with the low-fat meal and 50% and 48%, respectively, with the high-fat meal.  The effect of food on betrixaban pharmacokinetics could be observed for up to 6 hours after meal intake.', 'high-fat foods', 'Moderate', 'The manufacturer recommends taking betrixaban at the same time each day with food.', '', '', 'DDInter', 0);
INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3563, 69, 'Acitretin', 'Ethanol consumption with acitretin leads to the formation of etretinate, which has a much longer half-life than acitretin.  Major human fetal abnormalities have been associated with the administration of acitretin, etretinate, and other retinoids.', 'alcohol', 'Major', 'Female patients should be warned that ethanol is contraindicated during active treatment with acitretin and for two months after cessation of therapy.', '', '', 'DDInter', 0);
