INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3571, 0, 'Bexarotene', 'Food may enhance the oral bioavailability of bexarotene.  In one clinical study, bexarotene peak plasma concentration (Cmax) and systemic exposure (AUC) resulting from a 75 to 300 mg dose were 35% and 48% higher, respectively, when administered after a fat-containing meal relative to a glucose solution.', 'high-fat foods', 'Moderate', 'Because safety and efficacy data are based upon administration with food, bexarotene should be administered once daily with a meal.  Patients may want to avoid consuming large amounts of grapefruit or grapefruit juice.', '', '', 'DDInter', 0);
