INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3940, 22487, 'Cefditoren', 'Food enhances the oral absorption and bioavailability of cefditoren pivoxil.', 'high-fat foods', 'Moderate', 'To ensure maximal oral absorption, cefditoren pivoxil should be administered with or immediately after a meal.', '', '', 'DDInter', 0);
INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3941, 13052, 'Carbinoxamine', 'Alcohol may potentiate some of the pharmacologic effects of CNS-active agents.  Use in combination may result in additive central nervous system depression and/or impairment of judgment, thinking, and psychomotor skills.', 'alcohol', 'Moderate', 'Patients receiving CNS-active agents should be warned of this interaction and advised to avoid or limit consumption of alcohol.  Ambulatory patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', '', '', 'DDInter', 0);
INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3942, 19797, 'Afatinib', 'Food may reduce the oral bioavailability of afatinib.  According to the product labeling, administration of afatinib with a high-fat meal decreased peak plasma concentration (Cmax) by 50% and systemic exposure (AUC) by 39% compared to administration in the fasted state.', 'high-fat foods', 'Moderate', 'Afatinib should be taken at least 1 hour before or 2 hours after a meal.', '', '', 'DDInter', 0);
INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3943, 0, 'Cariprazine', 'Alcohol may potentiate some of the pharmacologic effects of central nervous system (CNS)-active agents.  Use in combination may result in additive CNS depression and/or impairment of judgment, thinking, and psychomotor skills.', 'alcohol', 'Moderate', 'Patients receiving CNS-active agents should be advised to avoid or limit consumption of alcohol.  Ambulatory patients should be counseled against driving, operating machinery, or engaging in potentially hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', '', '', 'DDInter', 0);
INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3944, 0, 'Cariprazine', 'Grapefruit juice may increase the plasma concentrations of cariprazine.  The proposed mechanism is inhibition of CYP450 3A4-mediated metabolism of cariprazine by certain compounds present in grapefruit.', 'grapefruit juice', 'Moderate', 'Patients should avoid the consumption of grapefruit and grapefruit juice during treatment with cariprazine.', '', '', 'DDInter', 0);
INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3945, 162, 'Adenosine', 'Caffeine and other xanthine derivatives (e.g., theophylline) are nonspecific, competitive antagonists of adenosine receptors and may interfere with the hemodynamic effects of adenosine.', 'coffee', 'Moderate', 'Clinicians should be aware that adenosine may be less effective in the presence of xanthine derivatives including caffeine.  Patients should avoid consumption of caffeine-containing products for at least 12 hours, preferably 24 hours, prior to administration of adenosine for myocardial perfusion imaging.', '', '', 'DDInter', 0);
