INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4001, 28282, 'Carbamazepine', 'Alcohol may potentiate some of the pharmacologic effects of carbamazepine.  Use in combination may result in additive central nervous system depression and/or impairment of judgment, thinking, and psychomotor skills.', 'alcohol', 'Moderate', 'Patients receiving carbamazepine should be advised to avoid or limit consumption of alcohol.  Given the drug''s narrow therapeutic index, patients receiving carbamazepine therapy should preferably avoid the regular consumption of grapefruits and grapefruit juice to prevent any undue fluctuations in plasma drug levels.  Patients should be advised to report signs of carbamazepine toxicity (nausea, visual disturbances, dizziness, or ataxia) to their physicians.', '', '', 'DDInter', 0);
