INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4497, 14119, 'Darifenacin', 'The consumption of grapefruit juice may be associated with increased plasma concentrations of darifenacin.  The mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruits.', 'grapefruit juice', 'Minor', 'Concomitant use of Darifenacin with grapefruit or grapefruit juice should generally be avoided.', '', '', 'DDInter', 0);
