INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4985, 2834, 'Entecavir', 'Food delays the oral absorption and reduces the oral bioavailability of entecavir.  According to the product labeling, administration of entecavir 0.5 mg with a standard high-fat meal or a light meal resulted in a delay in absorption by 0.25 to 0.75 hours, a decrease in the peak plasma concentration (Cmax) by 44% to 46%, and a decrease in the area under the plasma concentration-time curve (AUC) by 18% to 20% compared to administration in the fasting state.', 'high-fat foods', 'Moderate', 'To ensure maximal oral absorption, entecavir should be administered on an empty stomach at least 2 hours after a meal and 2 hours before the next meal.', '', '', 'DDInter', 0);
