INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (6143, 0, 'Istradefylline', 'Smoking tobacco may decrease the steady-state systemic exposure of istradefylline by 38% to 54%.', 'cigarette', 'Moderate', 'The possibility of reduced therapeutic effects of istradefylline should be considered in smokers.  The manufacturer recommends an istradefylline dosage of 40 mg once daily in patients who smoke 20 or more cigarettes (or the equivalent amount of another tobacco product) per day.', '', '', 'DDInter', 0);
INSERT INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (6144, 2053, 'Isosorbide dinitrate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation.  Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'alcohol', 'Moderate', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.  Some authorities recommend avoiding alcohol in patients receiving vasodilating antihypertensive drugs.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', '', '', 'DDInter', 0);
