INSERT OR IGNORE INTO food_interactions (id, med_id, interaction, source, created_at, trade_name) VALUES (214, 0, 'Interaction Type: food
Severity: Moderate

Effect: Food may interfere with the gastrointestinal absorption of penicillamine.

Management: Penicillamine should be administered on an empty stomach, at least one hour before or two hours after meals, and at least one hour apart from any other drug, food, or milk.  This permits maximum absorption and reduces the likelihood of inactivation by metal binding in the gastrointestinal tract.', 'DDInter', 0, NULL);
