INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (104, 21268, 'Kava', 'The combination of kava and alcohol poses a theoretical risk of additive hepatotoxicity during chronic concomitant use.  Studies in mice also suggest that the hypnotic effects of kava and alcohol may be synergistically increased when taken together, and that alcohol can markedly potentiate the toxicity of kava.', 'alcohol', 'Moderate', 'In general, patients should consult a healthcare provider before taking any herbal or alternative medicine.  Use of kava-containing products with alcohol should be avoided.  Patients using kava should be instructed to discontinue the drug promptly and seek medical attention if signs and symptoms of hepatic injury develop, including fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice.', NULL, NULL, 'DDInter', 0);
