INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (133, 0, 'Ivosidenib', 'Grapefruit juice may increase the plasma concentrations of ivosidenib.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.Inhibition of hepatic CYP450 3A4 may also contribute.  The interaction has not been studied with grapefruit juice.', 'grapefruit juice', 'Major', 'Ivosidenib may be administered with or without food, but should not be administered with a high-fat meal.  Patients should avoid consumption of grapefruit and grapefruit juice during treatment with ivosidenib.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (134, 0, 'Abacavir', 'Consumption of ethanol during treatment with abacavir results in a decrease in elimination of abacavir due to the inhibition of alcohol dehydrogenase.', 'alcohol', 'Minor', 'Consumption of ethanol during treatment with abacavir results in a decrease in elimination of abacavir due to the inhibition of alcohol dehydrogenase.  Coadministration of alcohol 0.7 g/kg (equivalent to 5 drinks) and abacavir resulted in a 41% increase in the area under the time concentration curve and a 26% increase in elimination half life.  The clinical significance of this interaction is unknown.', NULL, NULL, 'DDInter', 0);
